function [Y]=learn_dcp_batch(ID,T,name)% function to learn a DCP in batch mode using a minimum jerk trajectory as% templateglobal dcps;% general parametersdt        = 0.001;goal      = T(1,size(T,2));tau       = 0.5;n_rfs     = 20;% initialize DMPdcp('clear',ID);dcp('init',ID,n_rfs,name,1);% initialize some variables for plottingZ=zeros(floor(2*tau/dt+1),2);X=Z;V=Z;T1=zeros(floor(2*tau/dt+1),3);Y=T1;PSI=zeros(floor(2*tau/dt+1),n_rfs);W=PSI;% generate the minimum jerk trajectoryt=0;td=0;tdd=0;% for i=0:2*tau/dt,%   [t,td,tdd]=min_jerk_step(t,td,tdd,goal,tau-i*dt,dt);%   T(i+1,:)   = [t td tdd];% end;if(size(T,2)>=1001)   T=T(1,1:1001);else    T(1,size(T,2):1001)=goal;endT=T';size(T);% use the in-built function to initialize the dcp with minjerk%dcp('Batch_Fit',ID,tau,dt,T(:,1),T(:,2),T(:,3));dcp('Change',ID,'y',T(1,1));dcp('Change',ID,'y0',T(1,1));dcp('Batch_Fit',ID,tau,dt,T(:,1));% test the fitdcp('reset_state',ID);dcp('Change',ID,'y',T(1,1));dcp('Change',ID,'y0',T(1,1));dcp('set_goal',ID,goal,1);for i=0:2*tau/dt,    [y,yd,ydd]=dcp('run',ID,tau,dt);    Z(i+1,:)   = [dcps(ID).z dcps(ID).zd];  Y(i+1,:)   = [y yd ydd];  X(i+1,:)   = [dcps(ID).x dcps(ID).xd];  V(i+1,:)   = [dcps(ID).v dcps(ID).vd];  PSI(i+1,:) = dcps(ID).psi';  W(i+1,:)   = dcps(ID).w';  end;  % % plotting time = (0:dt:tau*2)';% % figure; clf;% % size(Y);% somma_acc=0;% for i=1:size(Y(:,3),1)%     somma_acc=somma_acc+Y(i,3)^2;% end% ID;% somma_acc;% plot position, velocity, acceleration vs. target%  subplot(431); plot(time,Y(:,1),'b -o',time, T(:,1),'r'); title('y'); aa=axis; axis([min(time) max(time) aa(3:4)]);% % vel=diffnc(T,0.001);% subplot(432);% plot(time,Y(:,2),'b -o', time,vel,'r');% title('yd');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(433);% plot(time,Y(:,3),'b -o',time, diffnc(vel,0.001),'r');% title('ydd');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % % plot internal states% subplot(434);% plot(time,Z(:,1));% title('z');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(435);% plot(time,Z(:,2));% title('zd');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(436);% plot(time,PSI);% title('Weighting Kernels');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(437);% plot(time,V(:,1));% title('v');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(438);% plot(time,V(:,2));% title('vd');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(439);% plot(time,W);% title('Linear Model Weights over Time');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(4,3,10);% plot(time,X(:,1));% title('x');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(4,3,11);% plot(time,X(:,2));% title('xd');% aa=axis;% axis([min(time) max(time) aa(3:4)]);% % subplot(4,3,12);% plot(W(end,:));% title('Weights');% xlabel(sprintf('tau=%f',tau));% % drawnow;